import React, { useEffect, useState } from "react";
import axios from "axios";
import toast from "react-hot-toast";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
    const { handleSubmit, register, reset } = useForm();
    const [articles, setArticles] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const [selectedArticle, setSelectedArticle] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = 5;
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    const displayedArticles = articles.slice(startIndex, endIndex);
    const totalPages = Math.ceil(articles.length / itemsPerPage);
    const [isLoading, setIsLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get("http://localhost:8080/admins/dashboard", {
                    headers: {
                        Authorization: JSON.parse(localStorage.getItem("ADMIN")),
                    },
                });
                console.log(response.data.message);
                toast.success(response.data.message);
            } catch (error) {
                console.error("Error fetching data:", error);
                toast.error("Error fetching data");
                navigate('/')
            } finally {
                setIsLoading(false);
            }
        };

        fetchData();
    }, []);
    useEffect(() => {
        axios.get(`http://localhost:3000/api/auth/allArticles`)
            .then((res) => {
                setArticles(res.data);
            })
            .catch((err) => console.log(err));
    }, []);
    const deleteArticle = (articleId) => {
        axios.delete(`http://localhost:3000/api/auth/deleteArticle/${articleId}`)
            .then((res) => {
                toast.success('suppression reussi');
                setArticles(articles.filter((articles) => articles.id !== articleId))
            })
            .catch((err) => console.log(err));
    };
    const onSubmit = (data) => {

        const newData = new FormData();

        const nameRegex = /^[A-Za-z\s]{1,100}$/;
        if (nameRegex.test(data.name)) {
            newData.append("name", data.name);
        } else {
            toast.error("Le champ 'name' est invalide.");
            return;
        }

        const descriptionRegex = /^[A-Za-z0-9\s.,!?]{1,100}$/;
        if (descriptionRegex.test(data.description)) {
            newData.append("description", data.description);
        } else {
            toast.error("Le champ 'description' est invalide.");
            return;
        }

        if (data.image && data.image[0] instanceof File) {
            newData.append("image", data.image[0]);
        } else {
            toast.error("Le champ 'image' est invalide.");
            return;
        }

        axios.post(`http://localhost:3000/api/auth/postArticle`, newData).then((res) => {
            toast.success('ajout reussi');
            setArticles([...articles, res.data]);
            navigate(0);
        }).catch((err) => {
            toast.error(err);
        })
        reset();
    };
    const goToPreviousPage = () => {
        if (currentPage > 1) {
            setCurrentPage(currentPage - 1);
        }
    };
    const goToNextPage = () => {
        if (currentPage < totalPages) {
            setCurrentPage(currentPage + 1);
        }
    };
    const goToIdPage = (id) => {
        if (id > 0 && id <= totalPages) {
            setCurrentPage(id);
        }
    };
    const handleShowModal = (article) => {
        setSelectedArticle(article);
        setShowModal(true);
    };
    const handleCloseModal = () => {
        setSelectedArticle([]);
        setShowModal(false);
    };
    return (
        <>
            <h1>Dashboard</h1>
            <div className="full">
                <h1 className="titreEdit">Edit Article</h1>
                <div className="containEdit">
                    <div className="cardEdit">
                        <h2>Ajoutez un article</h2>
                        <form onSubmit={handleSubmit(onSubmit)} encType="multipart/form-data">
                            <label className="form-label">Titre</label>
                            <input type="text" className="form-control" {...register("name")} />
                            <label className="form-label">Description</label>
                            <input type="text" className="form-control" {...register("description")} />
                            <label htmlFor="formFile" className="form-label"> Image </label>
                            <input id="file-input" className="form-control" type="file" accept="image/*" {...register("image")} />
                            <button type="submit" className="btn btn-primary py-1 my-3 w-100">
                                Submit
                            </button>
                        </form>
                    </div>
                    <div className="listEdit">
                        {displayedArticles.map((article) => (
                            <div className="cardArticle" key={article.id} >
                                <img className="imgArticle" src={`http://localhost:3000/images/${article.image}`} alt={article.title} />
                                <div className="descArticle">
                                    <h2>{article.name}</h2>
                                    <p>{article.description}</p>
                                </div>
                                <div className="actionArticle">
                                    <img className="p-2" src={Delete} alt="delete" onClick={() => deleteArticle(article.id)} />
                                    <img className="p-2" src={Edit} alt="edition" onClick={() => handleShowModal(article)} />
                                    <ModalEdit
                                        show={showModal}
                                        handleClose={handleCloseModal}
                                        data={selectedArticle}
                                        setArticles={setArticles}
                                    />
                                </div>
                            </div>
                        ))}
                    </div>
                    <nav className="navPagination" aria-label="Page navigation example">
                        <ul className="pagination">
                            <li className="page-item" onClick={goToPreviousPage}>
                                <div className="page-link" aria-label="Previous">
                                    <span aria-hidden="true">&laquo;</span>
                                    <span className="sr-only"> Previous</span>
                                </div>
                            </li>
                            <li className="page-item"><span className="page-link" onClick={() => goToIdPage(1)}>1</span></li>
                            <li className="page-item"><span className="page-link" onClick={() => goToIdPage(2)}>2</span></li>
                            <li className="page-item"><span className="page-link" onClick={() => goToIdPage(3)}>3</span></li>
                            <li className="page-item" onClick={goToNextPage}>
                                <div className="page-link" aria-label="Next"  >
                                    <span className="sr-only">Next </span>
                                    <span aria-hidden="true">&raquo;</span>
                                </div>
                            </li>
                        </ul>
                    </nav>
                </div>
            </div>
        </>
    );
}

export default Dashboard;
