import React, { Component } from "react";
import { Container, Row } from "reactstrap";

//Import Section Title
import SectionTitle from "../common/section-title";

//Import Pricing
import FormationBox from "./formation-box";

class Formations extends Component {
  state = {
    formations: [
      {
        id: 1,
        title: "Project 1",
        image: "../../../public/assets/images/projet.jpg",
        description: "description",
        features: {
          bandwith: "1GB",
          onlinespace: "50MB",
          support: "No",
          domain: "1",
          hiddenFees: "No",
        },
      },
      {
        id: 2,
        title: "Project 2",
        image: "../../../public/assets/images/projet.jpg",
        description: "description",
        isRibbon: true,
        features: {
          bandwith: "10GB",
          onlinespace: "500MB",
          support: "Yes",
          domain: "10",
          hiddenFees: "No",
        },
      },
      {
        id: 3,
        title: "Project 3",
        image: "../../../public/assets/images/projet.jpg",
        description: "description",
        features: {
          bandwith: "100GB",
          onlinespace: "2 GB",
          support: "Yes",
          domain: "Unlimited",
          hiddenFees: "No",
        },
      },
    ],
  };
  render() {
    return (
      <React.Fragment>
        <section className="section bg-light" id="pricing">
          <Container>
            {/* Render section title */}
            <SectionTitle
              title="Formations"
              description="Retrouvez ici la liste complète de mes formations, que vous êtes invités à explorer et à visiter pour découvrir mon travail créatif et mes réalisations."
            />

            <Row className="mt-5">
              {/* Render Pricing Box */}
              {this.state.pricings.map((pricing, key) => (
                <PricingBox key={key} pricing={pricing} />
              ))}
            </Row>
          </Container>
        </section>
      </React.Fragment>
    );
  }
}

export default Pricing;
