import React, { useEffect, useState } from "react";
import axios from "axios";
import toast from "react-hot-toast";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import { Modal, Button } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css"; // Assurez-vous d'importer la feuille de style Bootstrap

const Dashboard = () => {
  const { handleSubmit, register, reset } = useForm();
  const [articles, setArticles] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedArticle, setSelectedArticle] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5;
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const displayedArticles = articles.slice(startIndex, endIndex);
  const totalPages = Math.ceil(articles.length / itemsPerPage);
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:8080/admins/dashboard", {
          headers: {
            Authorization: JSON.parse(localStorage.getItem("ADMIN")),
          },
        });
        console.log(response.data.message);
        toast.success(response.data.message);
      } catch (error) {
        console.error("Error fetching data:", error);
        toast.error("Error fetching data");
        navigate('/');
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    axios.get(`http://localhost:8080/projects/get`)
      .then((res) => {
        setArticles(res.data);
      })
      .catch((err) => console.log(err));
  }, []);

  const deleteArticle = (articleId) => {
    axios.delete(`http://localhost:3000/api/auth/deleteArticle/${articleId}`)
      .then((res) => {
        toast.success('suppression reussie');
        setArticles(articles.filter((article) => article.id !== articleId))
      })
      .catch((err) => console.log(err));
  };

  const onSubmit = (data) => {
    // ... (le reste de votre code reste inchangÃ©)
  };

  const goToPreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const goToNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const goToIdPage = (id) => {
    if (id > 0 && id <= totalPages) {
      setCurrentPage(id);
    }
  };

  const handleShowModal = (article) => {
    setSelectedArticle(article);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setSelectedArticle([]);
    setShowModal(false);
  };

  return (
    <>
      <h1 className="mt-4">Dashboard</h1>
      <div className="container mt-4">
        <h1 className="titreEdit">Edit Article</h1>
        <div className="row">
          <div className="row">
            <div className="card">
              <div className="card-body">
                <h2 className="card-title">Ajoutez un article</h2>
                <form onSubmit={handleSubmit(onSubmit)} encType="multipart/form-data">
                  <div className="mb-3">
                    <label htmlFor="name" className="form-label">Titre</label>
                    <input type="text" className="form-control" {...register("name")} />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="description" className="form-label">Description</label>
                    <input type="text" className="form-control" {...register("description")} />
                  </div>
                  <div className="mb-3">
                    <label htmlFor="image" className="form-label">Image</label>
                    <input id="file-input" className="form-control" type="file" accept="image/*" {...register("image")} />
                  </div>
                  <button type="submit" className="btn btn-primary py-1 my-3 w-100">
                    Submit
                  </button>
                </form>
              </div>
            </div>
          </div>
          <div className="row">
            {displayedArticles.map((article, index) => (
              <div className="card mb-3" key={index} style={{textAlign:'center', width:'auto', margin:'15px'}}>
                <img className="card-img-top" src={`http://localhost:3000/${article.image}`} alt={article.title} style={{width:'500px', margin:'1px auto'}}/>
                <div className="card-body">
                  <h5 className="card-title">{article.name}</h5>
                  <p className="card-text">{article.description}</p>
                  <div className="btn-group">
                    <Button variant="danger" onClick={() => deleteArticle(article.id)}>Supprimer</Button>
                    <Button variant="info" onClick={() => handleShowModal(article)}>Ãditer</Button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
        <nav className="navPagination" aria-label="Page navigation example">
          <ul className="pagination">
            <li className={`page-item ${currentPage === 1 ? 'disabled' : ''}`} onClick={goToPreviousPage}>
              <button className="page-link" aria-label="Previous">
                <span aria-hidden="true">&laquo;</span>
                <span className="sr-only">Previous</span>
              </button>
            </li>
            {[...Array(totalPages)].map((_, index) => (
              <li className={`page-item ${index + 1 === currentPage ? 'active' : ''}`} key={index}>
                <button className="page-link" onClick={() => goToIdPage(index + 1)}>{index + 1}</button>
              </li>
            ))}
            <li className={`page-item ${currentPage === totalPages ? 'disabled' : ''}`} onClick={goToNextPage}>
              <button className="page-link" aria-label="Next">
                <span aria-hidden="true">&raquo;</span>
                <span className="sr-only">Next</span>
              </button>
            </li>
          </ul>
        </nav>
      </div>

      {/* Ajout du Modal Bootstrap */}
      <Modal show={showModal} onHide={handleCloseModal}>
        {/* Le contenu de votre modal va ici */}
      </Modal>
    </>
  );
}

export default Dashboard;
