import React, { Component } from "react";
import { Container, Row, Col, Card, CardImg, CardBody, CardTitle, CardSubtitle, CardText } from "reactstrap";
import axios from "axios";
import SectionTitle from "../common/section-title";
import ProjectBox from "./project-box";

class Projects extends Component {
  state = {
    projects: [],
    isLoading: true,
  };

  componentDidMount() {
    axios.get("http://localhost:8080/projects/get")
      .then(response => {
        this.setState({ projects: response.data, isLoading: false });
      })
      .catch(error => {
        console.error("Error fetching project data:", error);
        this.setState({ isLoading: false });
      });
  }

  render() {
    return (
      <React.Fragment>
        <section className="section bg-light active" id="projects">
          <Container>
            <SectionTitle
              title="Projects"
              description="Retrouvez ici la liste complète de mes projets, que vous êtes invités à explorer et à visiter pour découvrir mon travail créatif et mes réalisations."
            />
            {this.state.isLoading ? (
              <p>Loading projects...</p>
            ) : (
              <Row className="mt-4">
                {this.state.projects.map((project, key) => (
                  <Col key={key} md={4}>
                    <Card className="mb-4">
                      <CardImg top width="100%" src={project.image} alt={project.title} />
                      <CardBody>
                        <CardTitle>{project.title}</CardTitle>
                        <CardSubtitle>{project.technologies.join(', ')}</CardSubtitle>
                        <CardText>{project.description}</CardText>
                        {/* Add any additional information or buttons as needed */}
                      </CardBody>
                    </Card>
                  </Col>
                ))}
              </Row>
            )}
          </Container>
        </section>
      </React.Fragment>
    );
  }
}

export default Projects;
