import React, { useEffect, useState } from "react";
import axios from "axios";
import toast from "react-hot-toast";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import { Modal } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css"; // Assurez-vous d'importer la feuille de style Bootstrap

const Dashboard = () => {
  const { handleSubmit, register, reset } = useForm();
  const [articles, setArticles] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedArticle, setSelectedArticle] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5;
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const displayedArticles = articles.slice(startIndex, endIndex);
  const totalPages = Math.ceil(articles.length / itemsPerPage);
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get("http://localhost:8080/admins/dashboard", {
          headers: {
            Authorization: JSON.parse(localStorage.getItem("ADMIN")),
          },
        });
        console.log(response.data.message);
        toast.success(response.data.message);
      } catch (error) {
        console.error("Error fetching data:", error);
        toast.error("Error fetching data");
        navigate('/');
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    axios.get(`http://localhost:8080/projects/get`)
      .then((res) => {
        setArticles(res.data);
      })
      .catch((err) => console.log(err));
  }, []);

  const deleteArticle = (articleId) => {
    axios.delete(`http://localhost:3000/api/auth/deleteArticle/${articleId}`)
      .then((res) => {
        toast.success('suppression reussie');
        setArticles(articles.filter((article) => article.id !== articleId))
      })
      .catch((err) => console.log(err));
  };

  const onSubmit = (data) => {
    // ... (le reste de votre code reste inchangé)
  };

  const goToPreviousPage = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const goToNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const goToIdPage = (id) => {
    if (id > 0 && id <= totalPages) {
      setCurrentPage(id);
    }
  };

  const handleShowModal = (article) => {
    setSelectedArticle(article);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setSelectedArticle([]);
    setShowModal(false);
  };

  return (
    <>
      <h1 className="mt-4">Dashboard</h1>
      <div className="container mt-4">
        {/* ... (le reste de votre code reste inchangé) */}
      </div>

      {/* Ajout du Modal Bootstrap */}
      <Modal show={showModal} onHide={handleCloseModal}>
        {/* Le contenu de votre modal va ici */}
      </Modal>
    </>
  );
}

export default Dashboard;
